exports.DEFAULT_CORRELATION_ID = 'NO_CORRELATION_ID';

/**
 * Returns true if the given correlation id is valid, false otherwise.
 *
 * @param {*} correlationId
 */
exports.isValid = correlationId => correlationId && typeof correlationId === 'string' && correlationId.length > 0;

/**
 * Returns a valid correlation Id that can be used by a logger. If the passed
 * value is not a valid correlation Id then the default correlation id is
 * returned
 *
 * @param {string} currentCorrelationId
 * @returns {string}
 */
exports.getValidCorrelationId = (currentCorrelationId) => {
  if (exports.isValid(currentCorrelationId)) {
    return currentCorrelationId;
  }
  return exports.DEFAULT_CORRELATION_ID;
};
