/**
 * API Catalog - series of APIs
 *
 * @param {string} endpoint
 * @param {APIParam[]} params
 */

module.exports = function APICatalog() {
  this.apis = {};
  this.add = (name, api) => {
    if (this.apis[name]) {
      throw new Error(`Error - catalog with name ${name} already exists`);
    }
    this.apis[name] = api;
    return api;
  };
  return this;
};
