/**
 * BusinessIntent - Maker's version of an intent. Brings intents through to Fulfilment.
 * @class
 *
 * @param {Object} businessIntent
 * @param {string} businessIntent.name - the name of the intent
 * @param {Maker.API} businessIntent.api - The API used to fulfill this business intent
 * @param {Maker.BusinessSlot[]} businessIntent.businessSlots - Business Slots associated with this intent - all slots are inherently required.
 * @param {String[]} businessIntent.businessRules - (Future)
 * @param {String[]} businessIntent.successMessages - an array of success messages which can be applied to this repository
 * @returns {Maker.BusinessIntent} BusinessIntent
 */
class BusinessIntent {
  constructor({
    name, api, businessSlots, businessRules, successMessages
  } = {}) {
    this.name = name;
    this.api = api;
    this.businessSlots = businessSlots;
    this.businessRules = businessRules;
    this.successMessages = successMessages;
  }
}

module.exports = BusinessIntent;
