/**
 * Conditional - returns true or false based on the output of a JEXL statement
 * @class
 *
 */
class Conditional {
  /**
     * Takes a JEXL statement to evaluate
     * @param {String} statement - something the user said https://github.com/TomFrost/jexl
     * @returns {Object}
     */
  constructor(statement) {
    // Please the JS purists - Jexl doesn't support tripple equals but we'll all write it outta habbit.
    let processedConditional = statement;
    processedConditional = processedConditional.replace(/===/g, '==');
    this.statement = processedConditional;
    this.type = 'conditional';
    return this;
  }
}

module.exports = Conditional;
