const ISO6391 = require('iso-639-1');


const validateISOCode = (ISOCode) => {
  if (!ISO6391.validate(ISOCode)) {
    throw new Error('Invalid ISO Code Specified');
  }
  return ISOCode;
};

/**
 * Language - Set the language of the maker bot
 * @class
 *
 * @param {Object} api
 * @param {string} api.ISOCode - The ISO 639-1 Code for the language
 * @returns {Maker.Language}
 */
class Language {
  constructor(ISOCode) {
    this.ISOCode = validateISOCode(ISOCode);
    return this;
  }
}

module.exports = Language;
