/**
 * DateRange - Maps to a range of dates.
 * @class
 * @memberof SlotType
 *
 * @param {Object} slottype
 * @param {boolean} slottype.allowFuture - Allow prediction of date ranges in the future? If set to false, a future month will be assumed to be the previous year.
 * @returns {Maker.SlotType.DateRange}
 */
class DateRange {
  constructor({ allowFuture = false }) {
    this.type = 'DateRange';
    this.allowFuture = allowFuture;
    this.internal = true;
    return this;
  }
}

module.exports = DateRange;
