/**
 * PlainText - Maps to a series of plaintext phrases.
 * @class
 * @memberof SlotType
 *
 * @param {Object} slottype
 * @param {boolean} slottype.name - A sensible name for this slot type, does not impact classification
 * @param {string[]} slottype.phrases - Phrases which may trigger this slottype
 * @returns {Maker.SlotType.PlainText}
 */
class PlainText {
  constructor({ name, phrases }) {
    this.internal = false; // gets created in AI engines
    this.name = name;
    this.phrases = phrases;
    this.type = 'PlainText';
  }
}

module.exports = PlainText;
