/**
 * RegEx - Regular expression slottype. Will also take the result of a capturing group.
 * @class
 * @memberof SlotType
 *
 * @param {Object} slottype
 * @param {boolean} slottype.name - A sensible name for this slot type, does not impact classification
 * @param {string} slottype.regex - A string representation of the regular expression. Omit the leading and trailing /
 * @param {string} slottype.flags - The flags to add to this regex. i for ignore case, g for global, m for multiline, etc.
 * @returns {Maker.SlotType.RegEx}
 */
class RegEx {
  constructor({ regex, flags, name }) {
    this.type = 'Regex';
    this.name = name;
    this.regex = regex;
    this.flags = flags;
    this.internal = true;
    return this;
  }
}

module.exports = RegEx;
