/**
 * Create an UserIntent
 *
 * @param {array} utterances - slotted utterances
 * @param {string} description - optional
 * @param {Maker.API} helperAPI - an API for evaluating the intent.
 * @returns {object} { status }
 * */
class UserIntent {
  constructor({
    name, description, utterances, helperAPI
  }) {
    this.name = name;
    this.utterances = utterances;
    this.description = description;
    this.helperAPI = helperAPI;
    // Maps UI slots to BI slots & their relevant resolver
    this.slotMap = undefined;
  }
}

module.exports = UserIntent;
