const Orchestrator = require('./Orchestrator');
const API = require('./API');
const APICatalog = require('./APICatalog');
const APIParam = require('./APIParam');
const BusinessIntent = require('./BusinessIntent');
const BusinessSlot = require('./BusinessSlot');
const ImportLib = require('./util/import');
const PromptStrategy = require('./PromptStrategy');
const ResponseVoice = require('./ResponseVoice');
const Response = require('./Response');
const Resolver = require('./Resolver');
const SlotType = require('./SlotType');
const UserIntent = require('./UserIntent');
const APIError = require('./APIError');
const Language = require('./Language');
const Conditional = require('./Conditional');

/**
 * @namespace Maker
 */
module.exports = function Maker() {
  const lib = {
    Maker: {
      Orchestrator,
      API,
      APICatalog,
      APIError,
      APIParam,
      BusinessIntent,
      BusinessSlot,
      Conditional,
      PromptStrategy,
      Resolver,
      ResponseVoice,
      Response,
      /**
       * @namespace SlotType
       */
      SlotType,
      UserIntent,
      Language
    }
  };
  const Import = ImportLib(lib.Maker);
  lib.Maker.importOrchestrator = Import;
  return lib;
};
