/*
  communicate with Dialogflow
  focused on mapping Orchestrator object into DF
*/
module.exports = function dialogflowOperatorInjector(logger, Operator) {
  return class DialogflowOperator extends Operator {
    constructor(params) {
      super(params);
      this.runtimeName = 'dialogflow';
      // Operator doesn't need this
      if (this.worker) {
        this.workerId = this.constructor.formatBotName(this.worker.Id || this.worker.id);
      }
    }
  };
};
