/*
  communicate with Amazon Lex.
  focused on mapping Orchestrator object into Lex
*/
module.exports = function lexOperatorInjector(logger, Operator) {
  return class LexOperator extends Operator {
    constructor(params) {
      super(params);
      this.runtimeName = 'lex';
      this.botName = this.constructor.formatBotName(this.bot.Name);
      if (this.worker) { // Operator does not need this
        this.workerId = this.constructor.formatBotName(this.worker.Id || this.worker.id);
      }
    }
  };
};
