const assert = require('assert');
const logger = require('@servisbot/sb-logger');
const ServisBotApiSdk = require('../../src/ServisBotApiSdk');

const api = ServisBotApiSdk(null, null, Promise, logger);

describe('BaaS', () => {
  it('should create an object data bag for the APIs', () => {
    const Alias = 'google';
    const Endpoint = 'http://google.ie';
    const RequestMapping = { paramA: '', paramB: '' };

    const myApi = new api.BaaS.API({
      Alias,
      Endpoint,
      RequestMapping
    });
    assert.equal(myApi.Alias, Alias);
    assert.equal(myApi.Endpoint, Endpoint);
    assert.equal(myApi.RequestMapping, RequestMapping);
  });
});
