const assert = require('assert');
const logger = require('@servisbot/sb-logger');
const OperatorLib = require('../../src/Operator/index');

const { Operator } = OperatorLib(logger);
const workerFixture = require('../fixtures/worker');


describe('Operator', () => {
  it('should create an Operator, and associate the properties', () => {
    const o = new Operator.Lex({
      worker: workerFixture,
      bot: { Name: 'testBot' }
    });
    assert.ok(o);
    assert.ok(o.targetConfig);
  });
});
